def matSearch( mat, x):
    # Complete this function
    n = len(mat)
    m = len(mat[0])
    low_row = 0
    high_col = m - 1
    while low_row <= n - 1  and high_col <= m - 1:
        if mat[low_row][high_col] == x:
            return True
        elif mat[low_row][high_col] > x:
            high_col -= 1
        else:
            low_row += 1

    return False
def print_matrix(matrix):
    for row in matrix:
        print("\t".join(map(str, row)))

